@use_kernel_forward_from_hub("RMSNorm")
class Qwen3RMSNorm(nn.Module):
    def __init__(self, hidden_size, eps: float = 1e-6) -> None:
        """
        Qwen3RMSNorm is equivalent to T5LayerNorm
        """
        super().__init__()
        self.weight = nn.Parameter(torch.ones(hidden_size))
        self.variance_epsilon = eps

    def forward(self, hidden_states: torch.Tensor) -> torch.Tensor:
        input_dtype = hidden_states.dtype
        hidden_states = hidden_states.to(torch.float32)
        variance = hidden_states.pow(2).mean(-1, keepdim=True)
        hidden_states = hidden_states * torch.rsqrt(variance + self.variance_epsilon)
        return self.weight * hidden_states.to(input_dtype)

    def extra_repr(self):
        return f"{tuple(self.weight.shape)}, eps={self.variance_epsilon}"


class Qwen3MLP(nn.Module):
    def __init__(self, config):
        super().__init__()
        self.config = config
        self.hidden_size = config.hidden_size
        self.intermediate_size = config.intermediate_size
        self.gate_proj = nn.Linear(self.hidden_size, self.intermediate_size, bias=False)
        self.up_proj = nn.Linear(self.hidden_size, self.intermediate_size, bias=False)
        self.down_proj = nn.Linear(self.intermediate_size, self.hidden_size, bias=False)
        self.act_fn = ACT2FN[config.hidden_act]

    def forward(self, x):
        down_proj = self.down_proj(self.act_fn(self.gate_proj(x)) * self.up_proj(x))
        return down_proj


class Qwen3RotaryEmbedding(nn.Module):
    inv_freq: torch.Tensor  # fix linting for `register_buffer`

    def __init__(self, config: Qwen3Config, device=None):
        super().__init__()
        self.max_seq_len_cached = config.max_position_embeddings
        self.original_max_seq_len = config.max_position_embeddings

        self.config = config

        self.rope_type = self.config.rope_parameters["rope_type"]
        rope_init_fn: Callable = self.compute_default_rope_parameters
        if self.rope_type != "default":
            rope_init_fn = ROPE_INIT_FUNCTIONS[self.rope_type]
        inv_freq, self.attention_scaling = rope_init_fn(self.config, device)

        self.register_buffer("inv_freq", inv_freq, persistent=False)
        self.original_inv_freq = inv_freq

    @staticmethod
    def compute_default_rope_parameters(
        config: Optional[Qwen3Config] = None,
        device: Optional["torch.device"] = None,
        seq_len: Optional[int] = None,
    ) -> tuple["torch.Tensor", float]:
        """
        Computes the inverse frequencies according to the original RoPE implementation
        Args:
            config ([`~transformers.PreTrainedConfig`]):
                The model configuration.
            device (`torch.device`):
                The device to use for initialization of the inverse frequencies.
            seq_len (`int`, *optional*):
                The current sequence length. Unused for this type of RoPE.
        Returns:
            Tuple of (`torch.Tensor`, `float`), containing the inverse frequencies for the RoPE embeddings and the
            post-processing scaling factor applied to the computed cos/sin (unused in this type of RoPE).
        """
        base = config.rope_parameters["rope_theta"]
        dim = getattr(config, "head_dim", None) or config.hidden_size // config.num_attention_heads

        attention_factor = 1.0  # Unused in this type of RoPE

        # Compute the inverse frequencies
        inv_freq = 1.0 / (
            base ** (torch.arange(0, dim, 2, dtype=torch.int64).to(device=device, dtype=torch.float) / dim)
        )
        return inv_freq, attention_factor

    @torch.no_grad()
    @dynamic_rope_update  # power user: used with advanced RoPE types (e.g. dynamic rope)
    def forward(self, x, position_ids):
        inv_freq_expanded = self.inv_freq[None, :, None].float().expand(position_ids.shape[0], -1, 1).to(x.device)
        position_ids_expanded = position_ids[:, None, :].float()

        device_type = x.device.type if isinstance(x.device.type, str) and x.device.type != "mps" else "cpu"
        with torch.autocast(device_type=device_type, enabled=False):  # Force float32
            freqs = (inv_freq_expanded.float() @ position_ids_expanded.float()).transpose(1, 2)
            emb = torch.cat((freqs, freqs), dim=-1)
            cos = emb.cos() * self.attention_scaling
            sin = emb.sin() * self.attention_scaling

        return cos.to(dtype=x.dtype), sin.to(dtype=x.dtype)


def rotate_half(x):
    """Rotates half the hidden dims of the input."""
    x1 = x[..., : x.shape[-1] // 2]
    x2 = x[..., x.shape[-1] // 2 :]
    return torch.cat((-x2, x1), dim=-1)


def apply_rotary_pos_emb(q, k, cos, sin, position_ids=None, unsqueeze_dim=1):
    """Applies Rotary Position Embedding to the query and key tensors.

    Args:
        q (`torch.Tensor`): The query tensor.
        k (`torch.Tensor`): The key tensor.
        cos (`torch.Tensor`): The cosine part of the rotary embedding.
        sin (`torch.Tensor`): The sine part of the rotary embedding.
        position_ids (`torch.Tensor`, *optional*):
            Deprecated and unused.
        unsqueeze_dim (`int`, *optional*, defaults to 1):
            The 'unsqueeze_dim' argument specifies the dimension along which to unsqueeze cos[position_ids] and
            sin[position_ids] so that they can be properly broadcasted to the dimensions of q and k. For example, note
            that cos[position_ids] and sin[position_ids] have the shape [batch_size, seq_len, head_dim]. Then, if q and
            k have the shape [batch_size, heads, seq_len, head_dim], then setting unsqueeze_dim=1 makes
            cos[position_ids] and sin[position_ids] broadcastable to the shapes of q and k. Similarly, if q and k have
            the shape [batch_size, seq_len, heads, head_dim], then set unsqueeze_dim=2.
    Returns:
        `tuple(torch.Tensor)` comprising of the query and key tensors rotated using the Rotary Position Embedding.
    """
    cos = cos.unsqueeze(unsqueeze_dim)
    sin = sin.unsqueeze(unsqueeze_dim)
    q_embed = (q * cos) + (rotate_half(q) * sin)
    k_embed = (k * cos) + (rotate_half(k) * sin)
    return q_embed, k_embed


def repeat_kv(hidden_states: torch.Tensor, n_rep: int) -> torch.Tensor:
    """
    This is the equivalent of torch.repeat_interleave(x, dim=1, repeats=n_rep). The hidden states go from (batch,
    num_key_value_heads, seqlen, head_dim) to (batch, num_attention_heads, seqlen, head_dim)
    """
    batch, num_key_value_heads, slen, head_dim = hidden_states.shape
    if n_rep == 1:
        return hidden_states
    hidden_states = hidden_states[:, :, None, :, :].expand(batch, num_key_value_heads, n_rep, slen, head_dim)
    return hidden_states.reshape(batch, num_key_value_heads * n_rep, slen, head_dim)


def eager_attention_forward(
    module: nn.Module,
    query: torch.Tensor,
    key: torch.Tensor,
    value: torch.Tensor,
    attention_mask: Optional[torch.Tensor],
    scaling: float,
    dropout: float = 0.0,
    **kwargs: Unpack[TransformersKwargs],
):
    key_states = repeat_kv(key, module.num_key_value_groups)
    value_states = repeat_kv(value, module.num_key_value_groups)

    attn_weights = torch.matmul(query, key_states.transpose(2, 3)) * scaling
    if attention_mask is not None:
        causal_mask = attention_mask[:, :, :, : key_states.shape[-2]]
        attn_weights = attn_weights + causal_mask

    attn_weights = nn.functional.softmax(attn_weights, dim=-1, dtype=torch.float32).to(query.dtype)
    attn_weights = nn.functional.dropout(attn_weights, p=dropout, training=module.training)
    attn_output = torch.matmul(attn_weights, value_states)
    attn_output = attn_output.transpose(1, 2).contiguous()

    return attn_output, attn_weights

import os  # éœ€è¦å¼•å…¥ os æ¨¡å—æ¥æ£€æŸ¥æ–‡ä»¶

class Qwen3Attention(nn.Module):
    """
    Multi-headed attention from 'Attention Is All You Need' paper
    Modified for KVSwap: Sparse Access & Compressed K Cache
    """

    def __init__(self, config: Qwen3Config, layer_idx: int):
        super().__init__()
        self.config = config
        self.layer_idx = layer_idx
        self.head_dim = getattr(config, "head_dim", config.hidden_size // config.num_attention_heads)
        self.num_key_value_groups = config.num_attention_heads // config.num_key_value_heads
        self.scaling = self.head_dim**-0.5
        self.attention_dropout = config.attention_dropout
        self.is_causal = True

        self.q_proj = nn.Linear(
            config.hidden_size, config.num_attention_heads * self.head_dim, bias=config.attention_bias
        )
        self.k_proj = nn.Linear(
            config.hidden_size, config.num_key_value_heads * self.head_dim, bias=config.attention_bias
        )
        self.v_proj = nn.Linear(
            config.hidden_size, config.num_key_value_heads * self.head_dim, bias=config.attention_bias
        )
        self.o_proj = nn.Linear(
            config.num_attention_heads * self.head_dim, config.hidden_size, bias=config.attention_bias
        )
        self.q_norm = Qwen3RMSNorm(self.head_dim, eps=config.rms_norm_eps)
        self.k_norm = Qwen3RMSNorm(self.head_dim, eps=config.rms_norm_eps)
        self.sliding_window = config.sliding_window if self.config.layer_types[layer_idx] == "sliding_attention" else None

        # ================= KVSwap: åˆå§‹åŒ–éƒ¨åˆ† =================
        self.kvswap_enabled = False
        # self.projection_matrix = None  # A Matrix: (H_k * d, r)
        self.compressed_k_cache = None # Lr K Cache: (Batch, Seq_Len, r)
        
        # åŠ è½½æŠ•å½±çŸ©é˜µ (è·¯å¾„éœ€è¦æ ¹æ®ä½ ç¬¬ä¸€æ­¥ä¿å­˜çš„ä½ç½®è°ƒæ•´)
        proj_path = f"/root/autodl-tmp/kvswap_projections/projection_layer_{layer_idx}.pt"
        if os.path.exists(proj_path):
            try:
                # åŠ è½½å¹¶è½¬ä¸ºå¯¹åº”çš„æ•°æ®ç±»åž‹å’Œè®¾å¤‡ï¼ˆå°†åœ¨ forward ä¸­ç§»åŠ¨åˆ° deviceï¼‰
                A = torch.load(proj_path, map_location="cpu") 
                self.register_buffer("projection_matrix", A.to(dtype=torch.float16)) # æ³¨å†Œä¸º buffer æ–¹ä¾¿ç®¡ç†
                self.kvswap_enabled = True
                
                # KVSwap è¶…å‚æ•° (å»ºè®®åŽç»­æ”¹ä¸ºä»Ž config è¯»å–)
                self.kv_group_size = 4    # G: ç»„å¤§å°
                self.kv_top_k_groups = 100 # M: é€‰ä¸­å¤šå°‘ç»„ (éœ€è¦æ ¹æ®æ˜¾å­˜é¢„ç®—è°ƒæ•´)
                
                # é¢„å¤„ç† A ç”¨äºŽ Query æŠ•å½±: Reshape [H_k * d, r] -> [H_k, d, r]
                # è¿™æ ·æ–¹ä¾¿åŽç»­åš Q_h * A_g(h)
                self.num_kv_heads = config.num_key_value_heads
                self.target_rank = A.shape[1]
                
            except Exception as e:
                print(f"Layer {layer_idx}: Failed to load KVSwap matrix: {e}")
        # ====================================================
    def _kvswap_predict_indices(self, query_states, full_key_states):
        """
        KVSwap æ ¸å¿ƒé€»è¾‘ï¼š
        1. æ›´æ–°åŽ‹ç¼©ç¼“å­˜
        2. è®¡ç®—è¿‘ä¼¼åˆ†æ•°
        3. è¿”å›ž Top-K ç»„çš„ç´¢å¼•
        """
        bsz, q_len, _, _ = query_states.shape
        
        # 1. è§£æžç»´åº¦ (è¿™æ˜¯å…¨é‡æ•°æ®ï¼ŒSeq_Len æ˜¯ 30000+)
        _, self.num_kv_heads, total_seq_len, self.head_dim = full_key_states.shape
        
        
        # 2. çŽ°åœºå…¨é‡åŽ‹ç¼© (On-the-fly Compression)
        # [B, H_k, S, D] -> [B, S, H_k, D] -> Flatten [B, S, Feature]
        k_flat = full_key_states.transpose(1, 2).reshape(bsz, total_seq_len, -1)
        
        if self.projection_matrix.dtype != k_flat.dtype:
            self.projection_matrix = self.projection_matrix.to(k_flat.dtype)
        if self.projection_matrix.device != k_flat.device:
            self.projection_matrix = self.projection_matrix.to(k_flat.device)
        # Project: [B, 1, H_k * d] @ [H_k * d, r] -> [B, 1, r]
        compressed_k = torch.matmul(k_flat, self.projection_matrix)
        
            
        # 3. è®¡ç®—ä½Žç§© Query (Head-wise å‡†å¤‡)
        # A: [H_k * d, r] -> [H_k, d, r]
        A_reshaped = self.projection_matrix.view(self.num_kv_heads, self.head_dim, -1) # -1 is target_rank
        
        # å¤„ç† GQA (Grouped Query Attention): å°† A æ‰©å±•åˆ° Query Heads çš„æ•°é‡
        # [H_k, d, r] -> [H_q, d, r]
        A_expanded = repeat_kv(A_reshaped.unsqueeze(0), self.num_key_value_groups).squeeze(0)
        
        # Q: [B, H_q, 1, d] -> permute -> [B, 1, H_q, d]
        # è®¡ç®— Q_lr: [B, 1, H_q, r]
        # å…¬å¼: Q_h * A_g(h)
        # è¿™é‡Œçš„å®žçŽ°åˆ©ç”¨å¹¿æ’­æœºåˆ¶: [B, 1, H_q, d] @ [H_q, d, r] -> [B, 1, H_q, r]
        q_permuted = query_states.transpose(1, 2) 
        q_lr = torch.einsum("bshd,hdr->bshr", q_permuted, A_expanded)
        
        # 4. è®¡ç®—åˆ†æ•°: Q_lr @ Compressed_K.T
        # [B, 1, H_q, r] @ [B, r, S] -> [B, 1, H_q, S]
        approx_scores = torch.matmul(q_lr, compressed_k.transpose(1, 2))
        # 5. Head-wise èšåˆ (æŠŠ Query Heads å½’ç±»åˆ° KV Heads)
        num_q_heads = approx_scores.shape[2] # 32
        gqa_group = num_q_heads // self.num_kv_heads # 4

        # Reshape: [B, 1, 8, 4, Seq]
        scores_view = approx_scores.view(bsz, 1, self.num_kv_heads, gqa_group, -1)
        
        # Sum: [B, 1, 8, Seq] -> å¾—åˆ°æ¯ä¸ª KV Head çš„ç‹¬ç«‹åˆ†æ•°
        agg_scores = scores_view.sum(dim=3)
        
        # Group Max Pooling
        # Pad åˆ°èƒ½è¢« group_size æ•´é™¤
        seq_len = agg_scores.shape[-1]
        # è®¡ç®—éœ€è¦è¡¥å¤šå°‘ä¸ª (31386 % 4 = 2ï¼Œæ‰€ä»¥éœ€è¦è¡¥ 4-2=2 ä¸ª)
        pad_len = (self.kv_group_size - (seq_len % self.kv_group_size)) % self.kv_group_size
        if pad_len > 0:
            agg_scores = torch.nn.functional.pad(agg_scores, (0, pad_len), value=-float('inf'))
        
        # 2. åˆ†ç»„ä¸Ž Max (é€»è¾‘ä¸å˜ï¼Œåªæ˜¯å¤šäº†ä¸€ä¸ª H_kv ç»´åº¦)
        num_groups = agg_scores.shape[-1] // self.kv_group_size

        # View: [B, 1, 8, Num_Groups, Group_Size]
        grouped_scores = agg_scores.view(bsz, 1, self.num_kv_heads, num_groups, self.kv_group_size)

        # Max: [B, 1, 8, Num_Groups] (æ¯ä¸ª KV Head æ¯ä¸ªç»„çš„ä»£è¡¨åˆ†)
        group_max_scores = grouped_scores.max(dim=-1).values
        
        # 3. TopK (åœ¨ Num_Groups ç»´åº¦å–)
        # ç»“æžœ: [B, 1, 8, k] -> æ¯ä¸ª KV Head ç‹¬ç«‹çš„ k ä¸ªç´¢å¼•
        k = min(self.kv_top_k_groups, num_groups)
        topk_indices = torch.topk(group_max_scores, k, dim=-1).indices # [B, 1, k]
        
        return topk_indices, seq_len # è¿”å›žåŽŸå§‹åºåˆ—é•¿åº¦ä»¥ä¾¿å¤„ç†è¶Šç•Œ

    def forward(
        self,
        hidden_states: torch.Tensor,
        position_embeddings: tuple[torch.Tensor, torch.Tensor],
        attention_mask: Optional[torch.Tensor],
        past_key_values: Optional[Cache] = None,
        cache_position: Optional[torch.LongTensor] = None,
        **kwargs: Unpack[FlashAttentionKwargs],
    ) -> tuple[torch.Tensor, Optional[torch.Tensor]]:
        input_shape = hidden_states.shape[:-1]
        hidden_shape = (*input_shape, -1, self.head_dim)

        query_states = self.q_norm(self.q_proj(hidden_states).view(hidden_shape)).transpose(1, 2)
        key_states = self.k_norm(self.k_proj(hidden_states).view(hidden_shape)).transpose(1, 2)
        value_states = self.v_proj(hidden_states).view(hidden_shape).transpose(1, 2)
        # print(f"DEBUG: Seq Len: {query_states.shape[2]}, KVSwap: {self.kvswap_enabled}")
        cos, sin = position_embeddings
        query_states, key_states = apply_rotary_pos_emb(query_states, key_states, cos, sin)

        # è¿™é‡Œçš„ past_key_values (HuggingFace Cache) æ¨¡æ‹Ÿæˆ‘ä»¬çš„â€œç£ç›˜â€
        if past_key_values is not None:
            cache_kwargs = {"sin": sin, "cos": cos, "cache_position": cache_position}
            # ðŸ”¥ key_states æ­¤æ—¶å˜æˆäº†åŒ…å« 8000+ Token çš„å…¨é‡åŽ†å²
            key_states, value_states = past_key_values.update(key_states, value_states, self.layer_idx, cache_kwargs)

        # ================= KVSwap é€»è¾‘æŽ¥å…¥ç‚¹ =================
        # ä»…åœ¨ Decoding é˜¶æ®µ (seq_len=1) ä¸” KVSwap å¯ç”¨æ—¶è§¦å‘
        is_decoding = query_states.shape[2] == 1 and past_key_values.get_seq_length(self.layer_idx) > 1
        if self.kvswap_enabled and is_decoding:
            # ðŸ”¥ðŸ”¥ðŸ”¥ã€æ ¸å¿ƒä¿®æ”¹ã€‘ç›´æŽ¥ä¼ å…¨é‡ key_states ðŸ”¥ðŸ”¥ðŸ”¥
            # ä¸è¦åˆ‡ç‰‡ï¼Œä¸è¦ new_kï¼Œä¸è¦ç»´æŠ¤çŠ¶æ€
            top_group_indices, total_seq_len = self._kvswap_predict_indices(query_states, key_states)
            
            # 2. æ¨¡æ‹Ÿä»Žç£ç›˜(Full Cache)åŠ è½½é€‰ä¸­çš„å—
            # top_group_indices: [B, 1, k] -> éœ€è¦å±•å¼€æˆ Token Indices
            # ä¾‹å¦‚ group_idx=2, size=4 -> indices [8, 9, 10, 11]
            bsz, _, num_kv_heads, k = top_group_indices.shape
            
            # 1. å±•å¼€ç´¢å¼• (å¤„ç† Group Size)
            offsets = torch.arange(self.kv_group_size, device=query_states.device)
            # Broadcasting: [B, 1, 8, k, 1] + [Group] -> [B, 1, 8, k, Group]
            #[æ¯ä¸ªç»„çš„èµ·å§‹ä½ç½®] + [ç»„å†…çš„ç›¸å¯¹åç§»é‡] = [è¯¥ç»„å†…æ‰€æœ‰ Token çš„å…·ä½“ä½ç½®]ã€‚
            #æŠŠ**â€œæˆ‘ä»¬è¦è¯»ç¬¬ 2 å—å’Œç¬¬ 5 å—â€è¿™ä¸ªæŒ‡ä»¤ï¼Œç¿»è¯‘æˆâ€œæˆ‘ä»¬è¦è¯»ç¬¬ 8, 9, 10, 11, 20, 21, 22, 23 å·æ•°æ®â€**
            token_indices = (top_group_indices.unsqueeze(-1) * self.kv_group_size) + offsets.view(1, 1, 1, 1, -1)
            # Flatten to: [B, 8, k*G] (åŽ»æŽ‰ç»´åº¦1ï¼Œå±•å¹³ kå’ŒG)
            token_indices = token_indices.view(bsz, num_kv_heads, -1)
            # è¿‡æ»¤æŽ‰ Padding å¯¼è‡´çš„è¶Šç•Œ Index (å¤„ç†æœ€åŽä¸€ç»„)
            # å¦‚æžœpadçš„ç´¢å¼•è¶…è¿‡äº†æœ€å¤§åˆæ³•çš„ç´¢å¼•ï¼Œå°±æŠŠå®ƒå¼ºåˆ¶ä¿®æ”¹ä¸ºæœ€å¤§åˆæ³•ç´¢å¼•ã€‚
            token_indices = token_indices.clamp(max=total_seq_len - 1)
            
            # åŠ ä¸Šå½“å‰æœ€æ–°çš„ token (Rolling Buffer ç®€åŒ–ç‰ˆï¼šæ€»æ˜¯ä¿ç•™æœ€æ–° token,ä¸ç®¡é¢„æµ‹å™¨é€‰äº†è°ï¼Œæ°¸è¿œæŠŠæœ€åŽä¸€ä¸ª Token åŠ ä¸Šã€‚é˜²æ­¢ä¸¢æŽ‰æœ€æ–°çš„åŽ†å²ä¿¡æ¯)
            # å®žé™…è®ºæ–‡ä¸­è¿˜æœ‰ä¸€ä¸ª Rolling Windowï¼Œè¿™é‡Œç®€åŒ–ä¸ºå¼ºåˆ¶åŒ…å«æœ€æ–° Token
            WINDOW_SIZE = 32 # ç»éªŒå€¼ï¼Œé€šå¸¸ 16-64 ä¹‹é—´
            
            # è®¡ç®—çª—å£èµ·å§‹ä½ç½® (æ³¨æ„ä¸è¦å°äºŽ0)
            start_idx = max(0, total_seq_len - WINDOW_SIZE)
            
            # ç”Ÿæˆçª—å£ç´¢å¼•: [start, start+1, ..., end-1]
            # Shape: [Window_Size]
            window_indices = torch.arange(start_idx, total_seq_len, device=query_states.device)
            
            # æ‰©å±•ç»´åº¦ä»¥åŒ¹é… Head-wise ç»“æž„
            # [Window_Size] -> [1, 1, Window_Size] -> [B, H_k, Window_Size]
            window_indices = window_indices.view(1, 1, -1).expand(bsz, num_kv_heads, -1)
            
            # æ‹¼æŽ¥: [KVSwapé¢„æµ‹å‡ºçš„ç´¢å¼•] + [æœ€è¿‘çš„32ä¸ªç´¢å¼•]
            # ç»“æžœå½¢çŠ¶: [B, H_k, k*G + Window_Size]
            token_indices = torch.cat([token_indices, window_indices], dim=-1)
            gather_indices = token_indices.unsqueeze(-1).expand(-1, -1, -1, self.head_dim)
            sparse_key_states = torch.gather(key_states, 2, gather_indices)
            sparse_value_states = torch.gather(value_states, 2, gather_indices)
            
            # 6. Attn
            sparse_key_states = repeat_kv(sparse_key_states, self.num_key_value_groups)
            sparse_value_states = repeat_kv(sparse_value_states, self.num_key_value_groups)
            
            attn_output = torch.nn.functional.scaled_dot_product_attention(
                query_states,
                sparse_key_states,
                sparse_value_states,
                attn_mask=None,
                dropout_p=0.0 if not self.training else self.attention_dropout,
                is_causal=False 
            )
            attn_weights = None
            
        else:
            attention_interface: Callable = eager_attention_forward
            if self.config._attn_implementation != "eager":
                attention_interface = ALL_ATTENTION_FUNCTIONS[self.config._attn_implementation]

            attn_output, attn_weights = attention_interface(
                self,
                query_states,
                key_states,
                value_states,
                attention_mask,
                dropout=0.0 if not self.training else self.attention_dropout,
                scaling=self.scaling,
                sliding_window=self.sliding_window,
                **kwargs,
            )
        # =======================================================================

        attn_output = attn_output.reshape(*input_shape, -1).contiguous()
        attn_output = self.o_proj(attn_output)
        return attn_output, attn_weights


class Qwen3DecoderLayer(GradientCheckpointingLayer):
    def __init__(self, config: Qwen3Config, layer_idx: int):
        super().__init__()
        self.hidden_size = config.hidden_size

        self.self_attn = Qwen3Attention(config=config, layer_idx=layer_idx)

        self.mlp = Qwen3MLP(config)
        self.input_layernorm = Qwen3RMSNorm(config.hidden_size, eps=config.rms_norm_eps)
        self.post_attention_layernorm = Qwen3RMSNorm(config.hidden_size, eps=config.rms_norm_eps)
        self.attention_type = config.layer_types[layer_idx]

    def forward(
        self,
        hidden_states: torch.Tensor,
        attention_mask: Optional[torch.Tensor] = None,
        position_ids: Optional[torch.LongTensor] = None,
        past_key_values: Optional[Cache] = None,
        use_cache: Optional[bool] = False,
        cache_position: Optional[torch.LongTensor] = None,
        position_embeddings: Optional[tuple[torch.Tensor, torch.Tensor]] = None,
        **kwargs: Unpack[TransformersKwargs],
    ) -> torch.Tensor:
        residual = hidden_states
        hidden_states = self.input_layernorm(hidden_states)
        # Self Attention
        hidden_states, _ = self.self_attn(
            hidden_states=hidden_states,
            attention_mask=attention_mask,
            position_ids=position_ids,
            past_key_values=past_key_values,
            use_cache=use_cache,
            cache_position=cache_position,
            position_embeddings=position_embeddings,
            **kwargs,
        )
        hidden_states = residual + hidden_states

        # Fully Connected
        residual = hidden_states
        hidden_states = self.post_attention_layernorm(hidden_states)
        hidden_states = self.mlp(hidden_states)
        hidden_states = residual + hidden_states
        return hidden_states


@auto_docstring
class Qwen3PreTrainedModel(PreTrainedModel):
    config: Qwen3Config
    base_model_prefix = "model"
    supports_gradient_checkpointing = True
    _no_split_modules = ["Qwen3DecoderLayer"]
    _skip_keys_device_placement = ["past_key_values"]
    _supports_flash_attn = True
    _supports_sdpa = True
    _supports_flex_attn = True

    _can_compile_fullgraph = True
    _supports_attention_backend = True
    _can_record_outputs = {
        "hidden_states": Qwen3DecoderLayer,
        "attentions": Qwen3Attention,
    }


@auto_docstring
class Qwen3Model(Qwen3PreTrainedModel):
    def __init__(self, config: Qwen3Config):
        super().__init__(config)
        self.padding_idx = config.pad_token_id
        self.vocab_size = config.vocab_size

        self.embed_tokens = nn.Embedding(config.vocab_size, config.hidden_size, self.padding_idx)
        self.layers = nn.ModuleList(
            [Qwen3DecoderLayer(config, layer_idx) for layer_idx in range(config.num_hidden_layers)]
        )
        self.norm = Qwen3RMSNorm(config.hidden_size, eps=config.rms_norm_eps)
        self.rotary_emb = Qwen3RotaryEmbedding(config=config)
        self.gradient_checkpointing = False
        self.has_sliding_layers = "sliding_attention" in self.config.layer_types

        # Initialize weights and apply final processing
        self.post_init()

    @check_model_inputs()
    @auto_docstring
    def forward(
        self,
        input_ids: Optional[torch.LongTensor] = None,
        attention_mask: Optional[torch.Tensor] = None,
        position_ids: Optional[torch.LongTensor] = None,
        past_key_values: Optional[Cache] = None,
        inputs_embeds: Optional[torch.FloatTensor] = None,
        use_cache: Optional[bool] = None,
        cache_position: Optional[torch.LongTensor] = None,
        **kwargs: Unpack[TransformersKwargs],
    ) -> BaseModelOutputWithPast:
        if (input_ids is None) ^ (inputs_embeds is not None):
            raise ValueError("You must specify exactly one of input_ids or inputs_embeds")

        if inputs_embeds is None:
            inputs_embeds = self.embed_tokens(input_ids)

        if use_cache and past_key_values is None:
            past_key_values = DynamicCache(config=self.config)

        if cache_position is None:
            past_seen_tokens = past_key_values.get_seq_length() if past_key_values is not None else 0
            cache_position = torch.arange(
                past_seen_tokens, past_seen_tokens + inputs_embeds.shape[1], device=inputs_embeds.device
            )

        if position_ids is None:
            position_ids = cache_position.unsqueeze(0)

        # It may already have been prepared by e.g. `generate`
        if not isinstance(causal_mask_mapping := attention_mask, dict):
            # Prepare mask arguments
            mask_kwargs = {
                "config": self.config,
                "input_embeds": inputs_embeds,
                "attention_mask": attention_mask,
                "cache_position": cache_position,
                "past_key_values": past_key_values,
                "position_ids": position_ids,
            }
            # Create the masks
            causal_mask_mapping = {
                "full_attention": create_causal_mask(**mask_kwargs),
            }
            # The sliding window alternating layers are not always activated depending on the config
            if self.has_sliding_layers:
                causal_mask_mapping["sliding_attention"] = create_sliding_window_causal_mask(**mask_kwargs)

        hidden_states = inputs_embeds
        position_embeddings = self.rotary_emb(hidden_states, position_ids)

        for decoder_layer in self.layers[: self.config.num_hidden_layers]:
            hidden_states = decoder_layer(
                hidden_states,
                attention_mask=causal_mask_mapping[decoder_layer.attention_type],
                position_embeddings=position_embeddings,
                position_ids=position_ids,
                past_key_values=past_key_values,
                use_cache=use_cache,
                cache_position=cache_position,
                **kwargs,
            )

        hidden_states = self.norm(hidden_states)
        return BaseModelOutputWithPast(
            last_hidden_state=hidden_states,
            past_key_values=past_key_values if use_cache else None,
        )


@auto_docstring
class Qwen3ForCausalLM(Qwen3PreTrainedModel, GenerationMixin):
    _tied_weights_keys = {"lm_head.weight": "model.embed_tokens.weight"}
    _tp_plan = {"lm_head": "colwise_rep"}
    _pp_plan = {"lm_head": (["hidden_states"], ["logits"])}

    def __init__(self, config):
        super().__init__(config)
        self.model = Qwen3Model(config)
        self.vocab_size = config.vocab_size
        self.lm_head = nn.Linear(config.hidden_size, config.vocab_size, bias=False)

        # Initialize weights and apply final processing
        self.post_init()

    @can_return_tuple
    @auto_docstring
    def forward(
        self,
        input_ids: Optional[torch.LongTensor] = None,
        attention_mask: Optional[torch.Tensor] = None,
        position_ids: Optional[torch.LongTensor] = None,
        past_key_values: Optional[Cache] = None,
        inputs_embeds: Optional[torch.FloatTensor] = None,
        labels: Optional[torch.LongTensor] = None,
        use_cache: Optional[bool] = None,
        cache_position: Optional[torch.LongTensor] = None,
        logits_to_keep: Union[int, torch.Tensor] = 0,
        **kwargs: Unpack[TransformersKwargs],
    ) -> CausalLMOutputWithPast:
        r"""
        labels (`torch.LongTensor` of shape `(batch_size, sequence_length)`, *optional*):
            Labels for computing the masked language modeling loss. Indices should either be in `[0, ...,
            config.vocab_size]` or -100 (see `input_ids` docstring). Tokens with indices set to `-100` are ignored
            (masked), the loss is only computed for the tokens with labels in `[0, ..., config.vocab_size]`.

        Example:

        ```python
        >>> from transformers import AutoTokenizer, Qwen3ForCausalLM

        >>> model = Qwen3ForCausalLM.from_pretrained("Qwen/Qwen3-8B")
        >>> tokenizer = AutoTokenizer.from_pretrained("Qwen/Qwen3-8B")

        >>> prompt = "Hey, are you conscious? Can you talk to me?"
        >>> inputs = tokenizer(prompt, return_tensors="pt")

        >>> # Generate
        >>> generate_ids = model.generate(inputs.input_ids, max_length=30)
        >>> tokenizer.batch_decode(generate_ids, skip_special_tokens=True, clean_up_tokenization_spaces=False)[0]
        "Hey, are you conscious? Can you talk to me?\nI'm not conscious, but I can talk to you."
        ```"""
        outputs: BaseModelOutputWithPast = self.model(
            input_ids=input_ids,
            attention_mask=attention_mask,
            position_ids=position_ids,
            past_key_values=past_key_values,
            inputs_embeds=inputs_embeds,
            use_cache=use_cache,
            cache_position=cache_position,
            **kwargs,
        )

        hidden_states = outputs.last_hidden_state
        # Only compute necessary logits, and do not upcast them to float if we are not computing the loss
        slice_indices = slice(-logits_to_keep, None) if isinstance(logits_to_keep, int) else logits_to_keep
        logits = self.lm_head(hidden_states[:, slice_indices, :])

        loss = None
        if labels is not None:
            loss = self.loss_function(logits=logits, labels=labels, vocab_size=self.config.vocab_size, **kwargs)

        return CausalLMOutputWithPast(
            loss=loss,
            logits=logits,
            past_key_values=outputs.past_key_values,
            hidden_states=outputs.hidden_states,
            attentions=outputs.attentions,
        )


